package net.fabricmc.wildmod_copper.registry;import net.fabricmc.wildmod_copper.blocks.OxidizingButton;import net.fabricmc.wildmod_copper.blocks.OxidizingPressurePlate;import net.fabricmc.wildmod_copper.blocks.WildCopper;import net.minecraft.block.AbstractBlock;import net.minecraft.block.Block;import net.minecraft.block.Blocks;import net.minecraft.block.Oxidizable;import net.minecraft.item.BlockItem;import net.minecraft.item.Item;import net.minecraft.item.ItemGroup;import net.minecraft.util.Identifier;import net.minecraft.util.registry.Registry;import static net.fabricmc.wildmod_copper.blocks.OxidizingButton.balanceOtherButtonTypes;public class BlockRegistry {  public static final OxidizingPressurePlate COPPER_PRESSURE_PLATE = new OxidizingPressurePlate(AbstractBlock.Settings.copy(Blocks.COPPER_BLOCK).noCollision(), Oxidizable.OxidationLevel.UNAFFECTED);  public static final OxidizingPressurePlate EXPOSED_COPPER_PRESSURE_PLATE = new OxidizingPressurePlate(AbstractBlock.Settings.copy(Blocks.COPPER_BLOCK).noCollision(), Oxidizable.OxidationLevel.EXPOSED);  public static final OxidizingPressurePlate WEATHERED_COPPER_PRESSURE_PLATE = new OxidizingPressurePlate(AbstractBlock.Settings.copy(Blocks.COPPER_BLOCK).noCollision(), Oxidizable.OxidationLevel.WEATHERED);  public static final OxidizingPressurePlate OXIDIZED_COPPER_PRESSURE_PLATE = new OxidizingPressurePlate(AbstractBlock.Settings.copy(Blocks.COPPER_BLOCK).noCollision(), Oxidizable.OxidationLevel.OXIDIZED);  public static final OxidizingButton COPPER_BUTTON = new OxidizingButton(AbstractBlock.Settings.copy(Blocks.COPPER_BLOCK).noCollision(), Oxidizable.OxidationLevel.UNAFFECTED);  public static final OxidizingButton EXPOSED_COPPER_BUTTON = new OxidizingButton(AbstractBlock.Settings.copy(Blocks.COPPER_BLOCK).noCollision(), Oxidizable.OxidationLevel.EXPOSED);  public static final OxidizingButton WEATHERED_COPPER_BUTTON = new OxidizingButton(AbstractBlock.Settings.copy(Blocks.COPPER_BLOCK).noCollision(), Oxidizable.OxidationLevel.WEATHERED);  public static final OxidizingButton OXIDIZED_COPPER_BUTTON = new OxidizingButton(AbstractBlock.Settings.copy(Blocks.COPPER_BLOCK).noCollision(), Oxidizable.OxidationLevel.OXIDIZED);  private static void register(Identifier id, Block block) {    Registry.register(Registry.BLOCK, id, block);    ItemRegistry.register(new BlockItem(block, new Item.Settings().group(ItemGroup.REDSTONE)));  }  public static void registerBlocks(){    register(COPPER_PRESSURE_PLATE.getIdentifier(), COPPER_PRESSURE_PLATE);    register(EXPOSED_COPPER_PRESSURE_PLATE.getIdentifier(), EXPOSED_COPPER_PRESSURE_PLATE);    register(WEATHERED_COPPER_PRESSURE_PLATE.getIdentifier(), WEATHERED_COPPER_PRESSURE_PLATE);    register(OXIDIZED_COPPER_PRESSURE_PLATE.getIdentifier(), OXIDIZED_COPPER_PRESSURE_PLATE);    register(COPPER_BUTTON.getIdentifier(), COPPER_BUTTON);    register(EXPOSED_COPPER_BUTTON.getIdentifier(), EXPOSED_COPPER_BUTTON);    register(WEATHERED_COPPER_BUTTON.getIdentifier(), WEATHERED_COPPER_BUTTON);    register(OXIDIZED_COPPER_BUTTON.getIdentifier(), OXIDIZED_COPPER_BUTTON);  }  public static void registerCoppers(){    WildCopper.registerOxidizable(Blocks.COPPER_BLOCK, Oxidizable.OxidationLevel.UNAFFECTED);    WildCopper.registerOxidizable(Blocks.EXPOSED_COPPER, Oxidizable.OxidationLevel.EXPOSED);    WildCopper.registerOxidizable(Blocks.WEATHERED_COPPER, Oxidizable.OxidationLevel.WEATHERED);    WildCopper.registerOxidizable(Blocks.OXIDIZED_COPPER, Oxidizable.OxidationLevel.OXIDIZED);    WildCopper.registerOxidizable(Blocks.WAXED_COPPER_BLOCK, Oxidizable.OxidationLevel.UNAFFECTED);    WildCopper.registerOxidizable(Blocks.WAXED_EXPOSED_COPPER, Oxidizable.OxidationLevel.EXPOSED);    WildCopper.registerOxidizable(Blocks.WAXED_WEATHERED_COPPER, Oxidizable.OxidationLevel.WEATHERED);    WildCopper.registerOxidizable(Blocks.WAXED_OXIDIZED_COPPER, Oxidizable.OxidationLevel.OXIDIZED);    WildCopper.registerOxidizable(Blocks.CUT_COPPER, Oxidizable.OxidationLevel.UNAFFECTED);    WildCopper.registerOxidizable(Blocks.EXPOSED_CUT_COPPER, Oxidizable.OxidationLevel.EXPOSED);    WildCopper.registerOxidizable(Blocks.WEATHERED_CUT_COPPER, Oxidizable.OxidationLevel.WEATHERED);    WildCopper.registerOxidizable(Blocks.OXIDIZED_CUT_COPPER, Oxidizable.OxidationLevel.OXIDIZED);    WildCopper.registerOxidizable(Blocks.WAXED_CUT_COPPER, Oxidizable.OxidationLevel.UNAFFECTED);    WildCopper.registerOxidizable(Blocks.WAXED_EXPOSED_CUT_COPPER, Oxidizable.OxidationLevel.EXPOSED);    WildCopper.registerOxidizable(Blocks.WAXED_WEATHERED_CUT_COPPER, Oxidizable.OxidationLevel.WEATHERED);    WildCopper.registerOxidizable(Blocks.WAXED_OXIDIZED_CUT_COPPER, Oxidizable.OxidationLevel.OXIDIZED);  }  public static void register() {    registerCoppers();    registerBlocks();    balanceOtherButtonTypes();  }}